import askMidoFormTypes from "./Search.Types";

const INITIAL_STATE = {
  searchVal: "",
  isSendingSearch: false,
  sendSearchState: false,
};
// CHANGE_SEARCH_VAL: "CHANGE_SEARCH_VAL",
//   SEND_SEARCH_START: "SEND_SEARCH_START",
//   SEND_SEARCH_SUCCESS: "SEND_SEARCH_SUCCESS",
//   SEND_SEARCH_FAILED: " SEND_SEARCH_FAILED",
const askMidoForm = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case askMidoFormTypes.CHANGE_QUESTION_VAL:
      return {
        ...state,
        searchVal: action.payload,
      };
    case askMidoFormTypes.SEND_QUESTION_START:
      return {
        ...state,
        isSendingSearch: true,
      };
    case askMidoFormTypes.SEND_QUESTION_SUCCESS:
      return {
        ...state,
        searchVal: "",
        isSendingSearch: false,
        sendSearchState: action.payload,
      };
    case askMidoFormTypes.SEND_QUESTION_FAILED:
      return {
        ...state,
        isSendingSearch: false,
        sendSearchState: action.payload,
      };
    default:
      return state;
  }
};

export default askMidoForm;
