import React from "react";
import { AiOutlineDoubleLeft, AiOutlineDoubleRight } from "react-icons/ai";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
export default function CarouselCardsComponent() {
  const settings = {
    dots: false,
    infinite: false,
    speed: 1000,
    slidesToShow: 6,
    slidesToScroll: 6,
    // rtl: true,
    prevArrow: (
      <AiOutlineDoubleLeft className="click_color  color-primary-xlight" />
    ),
    nextArrow: (
      <AiOutlineDoubleRight className="click_color  color-primary-xlight" />
    ),
    responsive: [
      {
        breakpoint: 1200,
        settings: {
          slidesToShow: 6,
          slidesToScroll: 6,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 3,
          slidesToShow: 3,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
        },
      },
    ],
  };
  return (
    <div>
      <Slider {...settings}>
        {section.map((item) => (
          <Col xs={12} className="p-0" key={item.id}>
            <div className="poster row__inners">
              <div
                className={
                  "small-carousel-img p-0 pb-1 pb-lg-0 tiles row_posters"
                }
              >
                vvvvvvvvvvvvvvv
                <div className="bg_imgs ">
                  <Link to="#">
                    <div>
                      <img
                        // src={
                        //   typeof item.cCover1 === "undefined"
                        //     ? process.env.REACT_APP_MEDIA_URL +
                        //       item.videoCover
                        //     : isTop
                        //     ? process.env.REACT_APP_MEDIA_URL + item.cPoster1
                        //     : process.env.REACT_APP_MEDIA_URL + item.mCover1
                        // }
                        alt="My image"
                      />
                    </div>
                  </Link>
                </div>
                <h6 className="d-lg-none movie_name_mobile text-white my-2  mx-1">
                  {/* {i18n.language === "en" ? item.nameEn : item.name} */}
                </h6>
              </div>
              <div className="carousel_data px-2 pb-3 d-none d-lg-block">
                <div className="d-flex carousel_icons">
                  <Link to="#">
                    <div className=" myauto ">
                      <MdPlayArrow className="play_icon_carousel  " />
                    </div>
                  </Link>
                </div>

                <div className="movie-label my-1">
                  <span className="mx-1 ">
                    {/* {moment(item.releaseDate).format("yyyy")} */}
                  </span>
                </div>
              </div>
            </div>
          </Col>
        ))}
      </Slider>
    </div>
  );
}
