import axios from "axios";
import searchTypes from "./Search.Types";

const { REACT_APP_BASE_URL, REACT_APP_APIKEY } = process.env;

const changeSearchVal = (newQuery) => ({
  type: searchTypes.CHANGE_SEARCH_VAL,
  payload: newQuery,
});

const sendSearchStart = () => ({
  type: searchTypes.SEND_SEARCH_START,
});

const sendSearchSuccess = (info) => ({
  type: searchTypes.SEND_SEARCH_SUCCESS,
  payload: info,
});

const sendSearchFailure = (error) => ({
  type: searchTypes.SEND_SEARCH_FAILED,
  payload: error,
});

const sendsearch = (question) => {
  return (dispatch) => {
    dispatch(sendQuestionStart());
    axios({
      method: "get",
      url: `${REACT_APP_BASE_URL}/search/movie?api_key=${REACT_APP_APIKEY}&query=${question}`,
    })
      .then((resp) => {
        console.log(resp);
        dispatch(sendQuestionSuccess(resp.data.message));
        if (resp.data.status === 200) {
          dispatch(sendQuestionSuccess(resp.data.message));
        } else {
          dispatch(sendQuestionFailure(resp.data));
        }
      })
      .catch((error) =>
        dispatch(sendQuestionFailure(error.response.data.result))
      );
  };
};

export { sendsearch, changeSearchVal };
