import React from "react";
import { AiOutlineDoubleLeft, AiOutlineDoubleRight } from "react-icons/ai";
import { MdPlayArrow } from "react-icons/md";
import { Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
export default function CarouselCardsComponent(props) {
  console.log(props, "ttttttttttttttttttttttttttttttttttt");
  const settings = {
    dots: false,
    infinite: false,
    speed: 1000,
    slidesToShow: 6,
    slidesToScroll: 6,
    // rtl: true,
    prevArrow: (
      <AiOutlineDoubleLeft className="click_color  color-primary-xlight" />
    ),
    nextArrow: (
      <AiOutlineDoubleRight className="click_color  color-primary-xlight" />
    ),
    responsive: [
      {
        breakpoint: 1200,
        settings: {
          slidesToShow: 6,
          slidesToScroll: 6,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 3,
          slidesToShow: 3,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
        },
      },
    ],
  };
  return (
    <div>
      {props.isLoading ? (
        <div>ddd</div>
      ) : (
        <Slider {...settings}>
          {props.cards.map((card) => (
            <Col xs={12} key={card.id}>
              <img
                src={process.env.REACT_APP_MEDIA_URL + card?.backdrop_path}
              />
            </Col>
          ))}
        </Slider>
      )}
    </div>
  );
}
