  return (
    <Container>
      <Row>
        <Col xs={6} md={4}>
          Cinema App
        </Col>
        <Col xs={6} md={4}>
          {/* <input type="text" placeholder="search" /> */}
          {/* <Form
            inline
            onSubmit={onSubmit}
            onChange={onChange}
            value={text}
            autoComplete="off"
            className=" pb-lg-0"
          >
            <input
              type="search"
              id="search"
              className="mb-5"

              onSubmit={onSubmit}
              onChange={onChange}
              value={text}
            />
          </Form> */}
          <SearchComponent />
        </Col>
        <Col xs={6} md={4}>
          <Dropdown>
            <Dropdown.Toggle variant="success" id="dropdown-basic">
              <FaUserCircle />
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
              <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
              <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
              <button onClick={handleLogOut}>sign out</button>
            </Dropdown.Menu>
          </Dropdown>
        </Col>
      </Row>
    </Container>
  );
}
