import React from "react";
import { Container, Row, Col, Form } from "react-bootstrap";
import { FaUserCircle } from "react-icons/fa";
import { useHistory } from "react-router-dom";
export default function NavbarComponent() {
  const [text, setText] = useState("");
  let history = useHistory();
  const onSubmit = (e) => {
    //   e.preventDefault();
    // alert(text);
    setText("");
    history.push(`/search/${text}`);
  };
  const onChange = (e) => {
    setText(e.target.value);
  };

  return (
    <Container>
      <Row>
        <Col xs={6} md={4}>
          Cinema App
        </Col>
        <Col xs={6} md={4}>
          <input type="text" placeholder="search" />
          <Form
            inline
            onSubmit={onSubmit}
            onChange={onChange}
            value={text}
            autoComplete="off"
            className=" pb-lg-0"
          >
            <input
              type="search"
              id="search"
              className="mb-5"

              // onSubmit={onSubmit}
              // onChange={onChange}
              // value={text}
            />
          </Form>
        </Col>
        <Col xs={6} md={4}>
          <div>
            <FaUserCircle />
          </div>
        </Col>
      </Row>
    </Container>
  );
}
