import searchTypes from "./Search.Types";

const INITIAL_STATE = {
  searchVal: "",
  isSendingsearchVal: false,
  sendsearchValState: false,
};

const searchReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case searchTypes.CHANGE_SEARCH_VAL:
      return {
        ...state,
        searchVal: action.payload,
      };
    case searchTypes.SEND_SEARCH_START:
      return {
        ...state,
        isSendingsearchVal: true,
      };
    case searchTypes.SEND_SEARCH_SUCCESS:
      return {
        ...state,
        searchVal: "",
        isSendingsearchVal: false,
        sendsearchValState: action.payload,
      };
    case searchTypes.SEND_SEARCH_FAILED:
      return {
        ...state,
        isSendingsearchVal: false,
        sendsearchValState: action.payload,
      };
    default:
      return state;
  }
};

export default searchReducer;
