import React, { useState, useEffect } from "react";
import { Form, Button, Container } from "react-bootstrap";
import { Link } from "react-router-dom";
import Cookies from "universal-cookie";
export default function Login() {
  const staticValue = { name: "Aya Magdy", password: "12345" };
  const [formValue, setFormValue] = useState({ username: "", password: "" });
  const [login, setLogin] = useState(false);
  const cookies = new Cookies();
  const form = `${formValue.username},${formValue.password}`;
  cookies.set("cinemaApp", form, {
    path: "/",
    maxAge: "43200",
  });
  cookies.get("cinemaApp");
  console.log(login, "ppppppppppppppppppppp");

  const updateField = (e) => {
    setFormValue({
      ...formValue,
      [e.target.name]: e.target.value,
    });
  };
  const printValues = (e) => {
    e.preventDefault();
    console.log(formValue.username, formValue.password);
  };

  //   const printValues = (e) => {
  //     e.preventDefault();
  //     console.log(form.username, form.password);
  //   };

  //   const updateField = (e) => {
  //     setState({
  //       ...form,
  //       [e.target.name]: e.target.value,
  //     });
  //   };
  return (
    <Container>
      <form onSubmit={printValues}>
        <input
          type="userName"
          name="username"
          placeholder="enter ur userName"
          className="w-25"
          value={formValue.username}
          onChange={updateField}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formValue.password}
          className="w-25"
          onChange={updateField}
        />

        <button onClick={setLogin(true)}>Submit</button>
      </form>
    </Container>
  );
}
