import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
export default function CarouselCardsComponent() {
  return (
    <div>
      <Slider {...settings}>
        {props.players.players.map((player) => (
          <Col xs={12} key={player.id}>
            <Link to={"/player-details/" + player.id}>
              <img
                src={process.env.REACT_APP_MEDIA_BASE_URL + player.image}
                className="rounded-circle mx-auto player_img"
                width="100px"
                height="100px"
                style={{ position: "relative" }}
              />
              <img
                src={process.env.REACT_APP_MEDIA_BASE_URL + player.sport_icon}
                style={{ position: "absolute", left: "20%", top: "50%" }}
                width="24px"
              />

              <h6 className="mx-auto mt-2 text-center color-tertiary-dark fs-sm">
                {player.ar_name}
              </h6>
              <p className="mx-auto color-secondary-xxxlight fs-xxs">
                {player.sport_ar}
              </p>
            </Link>
          </Col>
        ))}
      </Slider>
    </div>
  );
}
