import axios from "axios";
import askMidoFormTypes from "./Search.Types";

const { REACT_APP_BASE_URL, REACT_APP_APIKEY } = process.env;

const changeQuestionVal = (e) => ({
  type: askMidoFormTypes.CHANGE_QUESTION_VAL,
  payload: e.target.value,
});

const sendQuestionStart = () => ({
  type: askMidoFormTypes.SEND_QUESTION_START,
});

const sendQuestionSuccess = (info) => ({
  type: askMidoFormTypes.SEND_QUESTION_SUCCESS,
  payload: info,
});

const sendQuestionFailure = (error) => ({
  type: askMidoFormTypes.SEND_QUESTION_FAILED,
  payload: error,
});

const sendAskMidoQuestion = (question) => {
  return (dispatch) => {
    dispatch(sendQuestionStart());
    axios({
      method: "get",
      url: `${REACT_APP_BASE_URL}/search/movie?api_key=${REACT_APP_APIKEY}&query=${question}`,
    })
      .then((resp) => {
        console.log(resp);
        dispatch(sendQuestionSuccess(resp.data.message));
        if (resp.data.status === 200) {
          dispatch(sendQuestionSuccess(resp.data.message));
        } else {
          dispatch(sendQuestionFailure(resp.data));
        }
      })
      .catch((error) =>
        dispatch(sendQuestionFailure(error.response.data.result))
      );
  };
};

export { sendAskMidoQuestion, changeQuestionVal };
