import askMidoFormTypes from "./askMidoForm.Types";

const INITIAL_STATE = {
  questionVal: "",
  isSendingQuestion: false,
  sendQuestionState: false,
};

const askMidoForm = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case askMidoFormTypes.CHANGE_QUESTION_VAL:
      return {
        ...state,
        questionVal: action.payload,
      };
    case askMidoFormTypes.SEND_QUESTION_START:
      return {
        ...state,
        isSendingQuestion: true,
      };
    case askMidoFormTypes.SEND_QUESTION_SUCCESS:
      return {
        ...state,
        questionVal: "",
        isSendingQuestion: false,
        sendQuestionState: action.payload,
      };
    case askMidoFormTypes.SEND_QUESTION_FAILED:
      return {
        ...state,
        isSendingQuestion: false,
        sendQuestionState: action.payload,
      };
    default:
      return state;
  }
};

export default askMidoForm;
