import React from "react";
import { connect } from "react-redux";
import { Container, Col, Form, Button } from "react-bootstrap";
import { changeSearchVal, Search } from "./../../redux/Search/Search.Action";
function SearchComponent() {
  return (
    <div>
      <Form className="text-center">
        <Form.Group className={`d-block`}>
          <Form.Control
            as="textarea"
            placeholder="أكتب سؤالك هنا"
            className="border-radius-15 fs-sm p-3 w-75 m-auto"
            onChange={(e) => changeSearchVal(e.target.value)}
            value={props.searchVal}
          />
        </Form.Group>
        <Button
          className={`m-auto py-2 py-md-2 cust-primary-btn fs-md w-50 d-block border-radius-10`}
          onClick={(e) => Search(props.search)}
          disabled={props.searchVal.length > 0 ? false : true}
        >
          {isSendingQuestion ? (
            <div className="text-center">
              <Spinner
                as="span"
                animation="grow"
                size="sm"
                role="status"
                aria-hidden="true"
              />
              <span className="mr-1">جاري الارسال...</span>
            </div>
          ) : (
            <span>إرسل</span>
          )}
        </Button>
      </Form>
    </div>
  );
}

const mapStateToProps = (state) => ({
  searchVal: state.searchVal.searchVal,
  isSendingsearchVal: state.searchVal.isSendingsearchVal,
  sendsearchValState: state.searchVal.sendsearchValState,
});

const mapDispatchToProps = (dispatch) => ({
  Search: (search) => dispatch(Search(search)),
  changeSearchVal: (newQuery) => dispatch(changeSearchVal(newQuery)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SearchComponent);
