import React from "react";
import { connect } from "react-redux";
import { Container, Spinner, Form, Button } from "react-bootstrap";
import {
  sendAskMidoQuestion,
  changeQuestionVal,
} from "./../../redux/Search/Search.Action";
function SearchComponent(props) {
  return (
    <div>
      <Form.Group className={`d-block`}>
        <Form.Control
          as="textarea"
          placeholder="أكتب سؤالك هنا"
          className="border-radius-15 fs-sm p-3 w-75 m-auto"
          onChange={(e) => changeQuestionVal(e.target.value)}
          value={props.questionVal}
        />
      </Form.Group>
      <Button
        className={`m-auto py-2 py-md-2 cust-primary-btn fs-md w-50 d-block border-radius-10`}
        onClick={(e) => sendAskMidoQuestion(props.questionVal)}
      >
        {props.isSendingQuestion ? (
          <div className="text-center">
            <Spinner
              as="span"
              animation="grow"
              size="sm"
              role="status"
              aria-hidden="true"
            />
            <span className="mr-1">جاري الارسال...</span>
          </div>
        ) : (
          <span>إرسل</span>
        )}
      </Button>
    </div>
  );
}

const mapStateToProps = (state) => ({
  questionVal: state.askMidoForm.questionVal,
  isSendingQuestion: state.askMidoForm.isSendingQuestion,
  sendQuestionState: state.askMidoForm.sendQuestionState,
});

const mapDispatchToProps = (dispatch) => ({
  sendAskMidoQuestion: (question) => dispatch(sendAskMidoQuestion(question)),
  changeQuestionVal: (newQuery) => dispatch(changeQuestionVal(newQuery)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SearchComponent);
